import React from 'react';
import './App.css';
import { Typography } from 'antd';
import { Input, Select, Button, Row, Card, Table, Popconfirm } from 'antd';
import 'antd/dist/antd.css';

const { Option } = Select;
const { Title } = Typography;
const { Search } = Input;

const buttonAfter = (
    <Button type="primary" icon="plus">
      Add
    </Button>

);



const columns = [
  {
    title: 'Steam Id',
    dataIndex: 'id',
    key: 'id',
  },
  {
    title: 'Action',
    key: 'action',
    render: (text, record) =>
        this.state.dataSource.length >= 1 ? (
            <Popconfirm title="Are you sure?" onConfirm={() => this.handleDelete(record.key)}>
              <a>Delete</a>
            </Popconfirm>
        ) : null,
  },
];



function App() {
  return (
    <div>
      <Title>Steam Group Game Checker</Title>


        <Card title="Search for common games"  style={{ width: 1000 }}>
          <Search
              addonBefore={"Steam Id:"}
              placeholder="Please enter a Steam Id..."
              enterButton={buttonAfter}
              size="large"
              onSearch={value => console.log(value)}
              style={{marginBottom: 16}}
          />

          <Row type="flex" justify="end">

          </Row>

          <Table  columns={columns} style={{marginBottom: 16}} />;

          <Button type="primary" icon="search">
            Search
          </Button>
        </Card>
    </div>
  );
}

export default App;
