import React from "react";
import {Button, Card, Popconfirm, Row, Table} from "antd";
class GroupGameSearchResultsPanel extends React.Component {
    constructor() {
        super();
        this.columns = [
            {
                title: 'Steam Id',
                dataIndex: 'id',
                key: 'id',
            },
            {
                title: 'Action',
                key: 'action',
                render: (text, record) =>
                    this.state.dataSource.length >= 1 ? (
                        <Popconfirm title="Are you sure?" onConfirm={() => this.handleDelete(record.id)}>
                            <a>Delete</a>
                        </Popconfirm>
                    ) : null,
            },
        ]
    }
    render() {
        return(
            <div>
                <Card title={<h2>Search Results</h2>} loading={false} style={{ width: 1000 }}>
                    <Table dataSource={this.props.dataSource} columns={this.columns} rowKey={record => record.id} scroll={{y:300}} pagination={false} />;
                </Card>
            </div>
        );
    }
}

export default GroupGameSearchResultsPanel
