import React from 'react';
import 'antd/dist/antd.css';
import { Form, Icon, Input, Button } from 'antd';


function hasErrors(fieldsError) {
    return Object.keys(fieldsError).some(field => fieldsError[field]);
}

class HorizontalLoginForm extends React.Component {
    componentDidMount() {
        // To disable submit button at the beginning.
        this.props.form.validateFields();
    }


    render() {
        const { getFieldDecorator, getFieldsError, getFieldError, isFieldTouched } = this.props.form;

        // Only show error after a field is touched.
        const input = isFieldTouched('input') && getFieldError('input');
        return (
            <Form layout="inline" onSubmit={this.props.onSubmit}>
                <Form.Item wrapperCol={{ sm: 24 }} style={{ width: "60%"}} validateStatus={input ? 'error' : ''} help={input || ''}>
                    {getFieldDecorator('input', {
                        rules: [{ required: this.props.required , message: this.props.requiredMessage }],
                    })(<Input addonBefore={this.props.label} style={{ width: '100%' }} />)}
                </Form.Item>

                <Form.Item >
                    <Button icon="plus" type="primary" htmlType="submit" disabled={hasErrors(getFieldsError())}>
                        Add
                    </Button>
                </Form.Item>
            </Form>
        );
    }
}

const WrappedHorizontalLoginForm = Form.create({ name: 'horizontal_login' })(HorizontalLoginForm);
export default WrappedHorizontalLoginForm;
