import React from "react";
import 'antd/dist/antd.css';
import { Input, Select, Button, Row, Card, Table, Popconfirm, Typography } from 'antd';
const { Option } = Select;
const { Title } = Typography;
const { Search } = Input;

const buttonAfter = (
    <Button type="primary" icon="plus">
        Add
    </Button>

);
const columns = [
    {
        title: 'Steam Id',
        dataIndex: 'id',
        key: 'id',
    },
    {
        title: 'Action',
        key: 'action',
        render: (text, record) =>
            this.state.dataSource.length >= 1 ? (
                <Popconfirm title="Are you sure?" onConfirm={() => this.handleDelete(record.key)}>
                    <a>Delete</a>
                </Popconfirm>
            ) : null,
    },
];

class GroupGameSearchPanel extends React.Component {
    render() {
        return(
        <div>
            <Title>Steam Group Game Checker</Title>
            
            <Card title="Search for common games"  style={{ width: 1000 }}>
            <Search
            addonBefore={"Steam Id:"}
            placeholder="Please enter a Steam Id..."
            enterButton={buttonAfter}
            size="large"
            onSearch={value => console.log(value)}
            style={{marginBottom: 16}}
            />

            <Table  columns={columns} />;
            <Row type="flex" justify="end">
                <Button type="primary" icon="search">
                    Search
                </Button>
            </Row>
            </Card>
        </div>
        );
    }
}
export default GroupGameSearchPanel;
