import React from "react";
import 'antd/dist/antd.css';
import { Input, Select, Button, Row, Card, Table, Popconfirm, Typography } from 'antd';
const { Option } = Select;
const { Title } = Typography;
const { Search } = Input;

const buttonAfter = (
    <Button type="primary" icon="plus" >
        Add
    </Button>

);


class GroupGameSearchPanel extends React.Component {
    constructor() {
        super();
        this.columns = [
            {
                title: 'Steam Id',
                dataIndex: 'id',
                key: 'id',
            },
            {
                title: 'Action',
                key: 'action',
                render: (text, record) =>
                    this.state.dataSource.length >= 1 ? (
                        <Popconfirm title="Are you sure?" onConfirm={() => this.handleDelete(record.id)}>
                            <a>Delete</a>
                        </Popconfirm>
                    ) : null,
            },
        ]

        this.state = {
            dataSource: [
            ],

        }
    }

    handleDelete = id => {
        const dataSource = [...this.state.dataSource];
        this.setState({ dataSource: dataSource.filter(item => item.id !== id) });
    };

    handleAdd = (value) =>{
        const { count, dataSource } = this.state;
        const newData = {
           id:value
        };
        this.setState({
            dataSource: [...dataSource, newData],
        });
    }

    handleSearch =() => {
        var steamIds = [];

        this.state.dataSource.forEach(function (item) {
            steamIds.push(item.id);
        });

        var data = {"steamIds":steamIds}

        var options = {
            method: 'POST', // *GET, POST, PUT, DELETE, etc.
            headers: {'Content-Type': 'application/json;'},
            body: JSON.stringify(data)
        }

        fetch('http://localhost:8080/users/',options)
            .then((response) => {return response.json()})
            .then((jsonResponse) => console.log(jsonResponse))
    }

    render() {
        return(
        <div>


            <Card title={<h2>Search for Common Games</h2>}  style={{ width: 1000 }}>

            <Search
            addonBefore={"Steam Id:"}
            placeholder="Please enter a Steam Id..."
            enterButton={buttonAfter}
            size="large"
            onSearch={value => this.handleAdd(value)}
            style={{marginBottom: 16}}
            />

            <Table dataSource={this.state.dataSource} columns={this.columns} rowKey={record => record.id} scroll={{y:300}} pagination={false} />;
            <Row type="flex" justify="end">
                <Button type="primary" icon="search" onClick={this.handleSearch}>
                    Search
                </Button>
            </Row>
            </Card>
        </div>
        );
    }
}
export default GroupGameSearchPanel;
